# Pattern 1: Remove duplicates while preserving order
def remove_duplicates_ordered(items):
    """Remove duplicates, preserve order."""
    seen = set()
    result = []
    for item in items:
        if item not in seen:
            seen.add(item)
            result.append(item)
    return result

